import React from 'react';
import './Page.css';  // Assuming you have a corresponding CSS file for styles
import img from './Image/m1.jpeg';  // Make sure the image path is correct

function Page() {
  return (
    <div className="page-container">
      <img src={img} alt="1kvar machine" /> {/* More descriptive alt text */}
      <h1>The 1kvar Machine</h1> {/* Improved heading for clarity */}
      <hr />
      <p>This page provides information about the 1kvar machine and helps solve your queries related to it.</p> {/* Directly using <p> */}
    </div>

    
  );
}

export default Page;
