import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './Slider.css';
// Import images
import img1 from './Image/4.jpeg';
import img2 from './Image/hav.webp';
import img3 from './Image/abb.webp';
import img4 from './Image/5.webp'; // Replace with a different image
import img5 from './Image/6.webp';
import img6 from './Image/7.webp';
import img7 from './Image/10.png';

// Slider data array
const sliderData = [
  { img: img1 },
  { img: img2 },
  { img: img3 },
  { img: img4 },
  { img: img5 },
  { img: img6 },
  { img: img7 },
];

const AutoSlider = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 300,
    autoplay: true,
    autoplaySpeed: 3000,
    slidesToShow: 4, // Show 4 slides at once
    slidesToScroll: 1,
    arrows: true, // Add arrows for manual navigation
    appendDots: dots => <ul className="slider-dots">{dots}</ul>,
  };

  return (
    <div className="slider-container">
      <Slider {...settings}>
        {sliderData.map((slide, index) => (
          <div key={index} className="slide">
            <img src={slide.img} alt={`Slide ${index + 1}`} />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default AutoSlider;
