import React, { useState } from 'react';
import Card from './Card';
import Content from './Content';
import Frontpage from './Frontpage';
import './Card.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; // Import for routing

const Main = () => {
  // Initialize state with one card
  const [cards, setCards] = useState([1]); // Start with one card

  // Function to remove a card by index
  const removeCard = (index) => {
    const updatedCards = cards.filter((_, i) => i !== index);
    setCards(updatedCards);
  };

  // Function to add a new card
  const addCard = () => {
    setCards([...cards, cards.length + 1]); // Add a new card with a unique identifier
  };

  return (
    <Router>
      <div>
        <Frontpage />
        <Routes>
          <Route path="/" element={<Content />} /> {/* Content on root path */}
          <Route path="/page/:machineId" element={<Page />} /> {/* Dynamic route for machines */}
        </Routes>
        <div className="cardComp">
          {cards.map((cardId, index) => (
            <Card key={cardId} cardId={cardId} onRemove={() => removeCard(index)} />
          ))}
        </div>
        <button onClick={addCard} className="add-card-button">Add Card</button>
      </div>
    </Router>
  );
};

export default Main;
