import React, { useEffect } from 'react';
import './Content.css';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';

import OwlCarousel from 'react-owl-carousel';
import vid from './Image/vid3.mp4';
import vid1 from './Image/vid4.mp4';
import video from './Image/vid5.mp4';

const Content = () => {
  useEffect(() => {
    // Add hover effects on owl-stage
    const owlStage = document.querySelector('.owl-stage');
    if (owlStage) {
      owlStage.addEventListener('mouseenter', () => {
        owlStage.style.transition = '0.5s';
        owlStage.style.transform = 'scale(1.05)';
      });

      owlStage.addEventListener('mouseleave', () => {
        owlStage.style.transition = '0.25s';
        owlStage.style.transform = 'translate3d(0px, 0px, 0px)';
      });
    }
  }, []);

  const stopScroll = () => {
    const scrollContent = document.querySelector('.scroll-content');
    if (scrollContent) scrollContent.style.animationPlayState = 'paused';
  };

  const startScroll = () => {
    const scrollContent = document.querySelector('.scroll-content');
    if (scrollContent) scrollContent.style.animationPlayState = 'running';
  };

  return (
    <div className="most">
      {/* Main Content */}
      <div className="grid-container">
        <div className="content">
          <h2>Main Content</h2>
          <p>This is the main content area where your primary content will go.</p>

          {/* Scrolling News Section */}
          <div
            className="scroll-container"
            onMouseOver={stopScroll}
            onMouseOut={startScroll}
          >
            <div className="scroll-content">
              {/* News Item 1 */}
              <div className="news-item">
                <div className="row mb-4">
                  <div className="col-sm-3 xs-box">
                    <img
                      src="assets/images/latest-news/abhimanch-dramatics-society.webp"
                      alt="Abhimanch The Dramatics Society Auditions"
                      className="w-100"
                      width="100"
                      height="100"
                    />
                  </div>
                  <div className="col-sm-9">
                    <div className="box-services-b">
                      <h3 className="title-small mb0">
                        <a href="https://www.powergen-india.com/summit-overview">
                          Summit Overview
                        </a>
                      </h3>
                      <small>
                        <i className="fa fa-clock-o mr10"></i>17th &amp; 18th
                        September 2024
                      </small>
                    </div>
                  </div>
                </div>
              </div>
              {/* Add more news items as needed */}
            </div>
          </div>
        </div>

        {/* Sidebar 1 */}
        <div className="sidebar">
          <h2>Information</h2>
          <video autoPlay muted loop className="sidebar-video" preload="auto">
            <source src={vid} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
          <p>This is a sidebar with additional information.</p>
        </div>

        {/* Sidebar 2 */}
        <div className="sidebar2">
          <h2>Projects</h2>
          <video autoPlay muted loop className="sidebar-video" preload="auto">
            <source src={vid1} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
          <p>This is a sidebar with additional information...</p>
        </div>

        {/* Services Section */}
        <div className="content2">
          <h2>Our Service</h2>
          <p>We provide the best service as you want.</p>
          <video autoPlay muted loop className="service-video" preload="auto">
            <source src={video} type="video/mp4" />
          </video>
        </div>

        {/* Team Section */}
        <div className="content2">
          <h2>Meet Our Team</h2>
          <p>Our dedicated team of experts.</p>
          <video autoPlay muted loop className="service-video" preload="auto">
            <source src={vid} type="video/mp4" />
          </video>
        </div>

        {/* Clients Section */}
        <div className="content2">
          <h2>Our Clients</h2>
          <p>We are trusted by leading companies.</p>
          <div className="client-links">
            <a
              href="https://solarhubenergy.com"
              target="_blank"
              rel="noopener noreferrer"
            >
              SolarHub
            </a>
            <br />
            <a
              href="https://www.jims.in/#"
              target="_blank"
              rel="noopener noreferrer"
            >
              Jims Sector 3
            </a>
            <br />
            <a
              href="https://nilkamal.com"
              target="_blank"
              rel="noopener noreferrer"
            >
              Nilkamal
            </a>
          </div>
        </div>
      </div>

      {/* Slider Section */}
      <div className="slider-section">
        <h2>Our Gallery</h2>
        <OwlCarousel
          className="owl-theme"
          loop
          margin={10}
          autoplay
          autoplayTimeout={3000}
          autoplayHoverPause
          items={3}
        >
          <div className="item">
            <img src="https://via.placeholder.com/300x200" alt="Slide 1" />
          </div>
          <div className="item">
            <img src="https://via.placeholder.com/300x200" alt="Slide 2" />
          </div>
          <div className="item">
            <img src="https://via.placeholder.com/300x200" alt="Slide 3" />
          </div>
          <div className="item">
            <img src="https://via.placeholder.com/300x200" alt="Slide 4" />
          </div>
        </OwlCarousel>
      </div>
    </div>
  );
};

export default Content;
