import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import img1 from './Image/4.jpeg';
import img2 from './Image/hav.webp';
import img3 from './Image/abb.webp';
import img4 from './Image/abb.webp';
import img5 from './Image/5.webp';
import img6 from './Image/6.webp';
import './Slider.css'; // Custom styling file

const AutoSlider = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    autoplay: true,
    autoplaySpeed: 3000,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: true, // Add arrows for manual navigation
    appendDots: dots => (
      <div style={{ position: 'absolute', bottom: '10px', width: '100%' }}>
        <ul style={{ display: 'flex', justifyContent: 'center' }}>{dots}</ul>
      </div>
    ),
  };

  return (
    <div className="slider-container">
      <h2 className="slider-title">Governing Bodies</h2>
      <Slider {...settings}>
        <div className="slide">
          <img src={img1} alt="Governing Body 1" />
          <div className="slide-description">
            <h3>Governing Body 1</h3>
            <p>Description of Governing Body 1</p>
          </div>
        </div>
        <div className="slide">
          <img src={img2} alt="Governing Body 2" />
          <div className="slide-description">
            <h3>Governing Body 2</h3>
            <p>Description of Governing Body 2</p>
          </div>
        </div>
        <div className="slide">
          <img src={img3} alt="Governing Body 3" />
          <div className="slide-description">
            <h3>Governing Body 3</h3>
            <p>Description of Governing Body 3</p>
          </div>
        </div>
      </Slider>
    </div>
  );
};

export default AutoSlider;
