import React from 'react';
import { Link } from 'react-router-dom'; // Import Link for routing
// import img from './Image/m1.jpeg'; // Image for Machine 1
// import img from './Image/m1.jpeg';  // Image for Machine 2
// import img1 from './Image/vid2.jpg';  // Image for Machine 3

function Card() {
  return (
    <div className='card-container' style={{ display: 'flex', gap: '20px', flexWrap: 'wrap', justifyContent: 'center' }}>
      {/* Card 1 */}
      <div className="card" style={{ width: '18rem' }}>
        <img src={img1} className="card-img-top" alt="Machine 1" />
        <div className="card-body">
          <h5 className="card-title">Machine 1</h5>
          <p className="card-text">This is Machine 1. It is designed for high performance and durability.</p>
          <Link to="/machine1" className="btn btn-primary">Learn More</Link>
        </div>
      </div>

      {/* Card 2 */}
      <div className="card" style={{ width: '18rem' }}>
        <img src={img2} className="card-img-top" alt="Machine 2" />
        <div className="card-body">
          <h5 className="card-title">Machine 2</h5>
          <p className="card-text">Machine 2 offers innovative solutions for complex tasks.</p>
          <Link to="/machine2" className="btn btn-primary">Learn More</Link>
        </div>
      </div>

      {/* Card 3 */}
      <div className="card" style={{ width: '18rem' }}>
        <img src={img3} className="card-img-top" alt="Machine 3" />
        <div className="card-body">
          <h5 className="card-title">Machine 3</h5>
          <p className="card-text">Discover the power and efficiency of Machine 3.</p>
          <Link to="/machine3" className="btn btn-primary">Learn More</Link>
        </div>
      </div>
    </div>
  );
}

export default Card;
