import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './Image/4.jpeg';
import './Image/hav.jpeg';
import './Image/4.jpeg';
const AutoSlider = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    autoplay: true,
    autoplaySpeed: 2000,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: true, // Add arrows for manual navigation
  };

  return (
    <div className="slider-container">
      <h2>Governing Bodies</h2> 
      <Slider {...settings}>
        <div>
          <img src="4.jpg" alt="Governing Body 1" />
          <p>Governing Body 1 Description</p> 
        </div>
        <div>
          <img src="hav.jpg" alt="Governing Body 2" />
          <p>Governing Body 2 Description</p> 
        </div>
        <div>
          <img src="abb.jpg" alt="Governing Body 3" />
          <p>Governing Body 3 Description</p> 
        </div>
      </Slider>
    </div>
  );
};

export default AutoSlider;
